/**
 * Choropleth implementation 06/03 - 23/03
 */
export class ChoroplethBingMaps extends Choropleth {

    /**
     * Creates an instance of Choropleth.
     * @param {BGMapWrapper} bgmap - Background map object.
     * @param {JSON} geometry - JSON object with the geometry that was read from the file.
     * @param {Object} userOptions - User options to be used.
     * @memberOf Choropleth
     */
    constructor(bgmap, geometry, userOptions) {
        super(bgmap, geometry, userOptions);
    }

    /**
     * M8) Setup all events used by the API. Right now the API uses: drag, zoom and click events.
     * Pan = viewchange,  Zoom = mousewheel, click = click
     * @param {number} mappos - This map id. 
     * @return {void}
     * @see http://www.bing.com/api/maps/sdkrelease/mapcontrol/isdk#addEventHandler+JS 
     * @see http://stackoverflow.com/a/8591192/5869289
     * @see http://stackoverflow.com/a/18675579/5869289
     */
    setupEvents(mappos) {
        Microsoft.Maps.Events.addHandler(this.bGMap.getBackgroundMapProviderObject(), 'mousewheel', () => { this.draw(); });
        Microsoft.Maps.Events.addHandler(this.bGMap.getBackgroundMapProviderObject(), 'viewchange', () => { this.draw(); });
        Microsoft.Maps.Events.addHandler(this.bGMap.getBackgroundMapProviderObject(), 'click', (e) => {
            let point = new Microsoft.Maps.Point(e.getX(), e.getY());
            let loc = e.target.tryPixelToLocation(point);
            const lon = loc.longitude;
            const lat = loc.latitude;
            
            if (this.rtree !== undefined) 
                this.searchRTree(lon, lat);
            if (this.kdtree !== undefined) 
                this.searchKdTree(lon, lat);
        });
    }
}

